function init(){for(var n=document.getElementById("snowContainer"),e=0;NUMBER_OF_snow>e;e++)n.appendChild(createAsnow())}function randomInteger(n,e){return n+Math.floor(Math.random()*(e-n))}function randomFloat(n,e){return n+Math.random()*(e-n)}function pixelValue(n){return n+"px"}function durationValue(n){return n+"s"}function createAsnow(){var n=document.createElement("div"),e=document.createElement("img");e.src="/public/images/eggnog/snow"+randomInteger(1,6)+".png",n.style.top="-100px",n.style.left=pixelValue(randomInteger(0,1e3));var t=Math.random()<.5?"clockwiseSpin":"counterclockwiseSpinAndFlip";n.style.webkitAnimationName="fade, drop",e.style.webkitAnimationName=t;var a=durationValue(randomFloat(5,11)),o=durationValue(randomFloat(4,8));n.style.webkitAnimationDuration=a+", "+a;var i=durationValue(randomFloat(0,5));return n.style.webkitAnimationDelay=i+", "+i,e.style.webkitAnimationDuration=o,n.appendChild(e),n}const NUMBER_OF_snow=30;window.addEventListener("load",init,!1);